"Change Set:		7978Monticello-nice.326Monticello-nice.326:make #classVarNames a sorted ArrayMonticello-nice.325:use #fasterKeys"!!MCClassDefinition methodsFor: 'accessing' stamp: 'nice 10/20/2009 22:03'!classVarNames	^(self selectVariables: #isClassVariable) asArray sort! !!MCFileBasedRepository methodsFor: 'as yet unclassified' stamp: 'nice 10/19/2009 22:15'!resizeCache: aDictionary	[aDictionary size <= self maxCacheSize] whileFalse:		[aDictionary removeKey: aDictionary fasterKeys atRandom]! !!MCPackageLoader methodsFor: 'private' stamp: 'nice 10/19/2009 22:53'!provisions	^ provisions ifNil: [provisions := Set withAll: Smalltalk fasterKeys]! !