"Change Set:		7990Installer-Core-nice.89Installer-Core-nice.89:Track keys usage, use asSet where dueInstaller-Core-nice.88:use #fasterKeys"!!Installer methodsFor: 'mantis' stamp: 'nice 10/19/2009 22:12'!bug: aBugNo"Installer mantis viewBug: 5639."	| page text | 	self setBug: aBugNo.		page := self maPage. 	text := String streamContents: [ :str |						#('Bug ID' 'Category' 'Severity' 'Reproducibility' 'Date Submitted' 			'Date Updated' 'Reporter' 'View Status' 'Handler' 			'Priority' 'Resolution' 'Status' 'Product Version' 'Summary' 'Description' 'Additional Information' ) 				do: [ :field |						| f |						f := self maRead: page field: field.			str nextPutAll: f key; nextPutAll: ': '; nextPutAll: f value; cr.		].		str nextPutAll: 'Notes: '; cr.		(self maReadNotes: page) do: [ :note | str nextPutAll: note; cr; cr ].				str nextPutAll: 'Files: '; nextPutAll: self maFiles fasterKeys printString.	]. 	^ text	! !!Installer methodsFor: 'websqueakmap' stamp: 'nice 10/20/2009 20:12'!wsmVersions| pkgAndVersion packageId packageName packageVersion versions |	pkgAndVersion := self smPackageAndVersion.	packageName := pkgAndVersion first.	packageVersion := pkgAndVersion last.	packageVersion isEmpty ifTrue: [ packageVersion := #latest ].	packageId := self wsmPackagesByName at: packageName.		versions := (self wsmReleasesFor: packageId) keys asSet.	versions remove: #latest.		^ versions collect: [ :version | self copy package: (packageName,'(', version ,')'); yourself ]. ! !