"Change Set:		8175CollectionsTests-nice.111CollectionsTests-nice.111:Some tests for cr/lf/crlf line delimiters"!!StringTest methodsFor: 'testing - lines' stamp: 'nice 11/16/2009 13:19'!testLinesDo	| sampleCRString sampleLFString sampleCRLFString lines |	sampleCRString := 'Fred', String cr , 'the' , String cr , 'Bear'.	sampleLFString := 'Fred', String lf , 'the' , String lf , 'Bear'.	sampleCRLFString := 'Fred', String crlf , 'the' , String crlf , 'Bear'.		lines := OrderedCollection new.	sampleCRString linesDo: [:aLine |		self deny: (aLine includes: Character cr).		self deny: (aLine includes: Character lf).		lines add: aLine].	self assert: lines asArray = #('Fred' 'the' 'Bear').		lines := OrderedCollection new.	sampleLFString linesDo: [:aLine |		self deny: (aLine includes: Character cr).		self deny: (aLine includes: Character lf).		lines add: aLine].	self assert: lines asArray = #('Fred' 'the' 'Bear').	lines := OrderedCollection new.	sampleCRLFString linesDo: [:aLine |		self deny: (aLine includes: Character cr).		self deny: (aLine includes: Character lf).		lines add: aLine].	self assert: lines asArray = #('Fred' 'the' 'Bear').! !!StringTest methodsFor: 'testing - lines' stamp: 'nice 11/16/2009 13:14'!testLineNumber	| sampleCRString sampleLFString sampleCRLFString |	sampleCRString := 'Fred', String cr , 'the' , String cr , 'Bear'.	sampleLFString := 'Fred', String lf , 'the' , String lf , 'Bear'.	sampleCRLFString := 'Fred', String crlf , 'the' , String crlf , 'Bear'.		self assert: (sampleCRString lineNumber: 2) = 'the'.	self assert: (sampleLFString lineNumber: 2) = 'the'.	self assert: (sampleCRLFString lineNumber: 2) = 'the'.! !!StringTest methodsFor: 'testing - lines' stamp: 'nice 11/16/2009 13:22'!testLineCorrespondingToIndex	| sampleCRString sampleLFString sampleCRLFString anIndex |	sampleCRString := 'Fred', String cr , 'the' , String cr , 'Bear'.	sampleLFString := 'Fred', String lf , 'the' , String lf , 'Bear'.	sampleCRLFString := 'Fred', String crlf , 'the' , String crlf , 'Bear'.		anIndex := sampleCRString indexOf: $h.	self assert: (sampleCRString lineCorrespondingToIndex: anIndex) = 'the'.	anIndex := sampleLFString indexOf: $h.	self assert: (sampleLFString lineCorrespondingToIndex: anIndex) = 'the'.	anIndex := sampleCRLFString indexOf: $h.	self assert: (sampleCRLFString lineCorrespondingToIndex: anIndex) = 'the'.		anIndex := sampleCRString indexOf: $B.	self assert: (sampleCRString lineCorrespondingToIndex: anIndex) = 'Bear'.	anIndex := sampleLFString indexOf: $B.	self assert: (sampleLFString lineCorrespondingToIndex: anIndex) = 'Bear'.	anIndex := sampleCRLFString indexOf: $B.	self assert: (sampleCRLFString lineCorrespondingToIndex: anIndex) = 'Bear'.		anIndex := sampleCRString indexOf: $d.	self assert: (sampleCRString lineCorrespondingToIndex: anIndex) = 'Fred'.	anIndex := sampleLFString indexOf: $d.	self assert: (sampleLFString lineCorrespondingToIndex: anIndex) = 'Fred'.	anIndex := sampleCRLFString indexOf: $d.	self assert: (sampleCRLFString lineCorrespondingToIndex: anIndex) = 'Fred'.! !!StringTest methodsFor: 'testing - lines' stamp: 'nice 11/16/2009 13:13'!testLineCount	| sampleCRString sampleLFString sampleCRLFString |	sampleCRString := 'Fred', String cr , 'the' , String cr , 'Bear'.	sampleLFString := 'Fred', String lf , 'the' , String lf , 'Bear'.	sampleCRLFString := 'Fred', String crlf , 'the' , String crlf , 'Bear'.		self assert: sampleCRString lineCount = 3.	self assert: sampleLFString lineCount = 3.	self assert: sampleCRLFString lineCount = 3.! !