"Change Set:		8469ToolBuilder-Morphic-jcg.45ToolBuilder-Morphic-jcg.45:Merge -ul.44 and -jcg.45"!!MorphicToolBuilder methodsFor: 'pluggable widgets' stamp: 'jcg 12/12/2009 10:43'!buildPluggableList: aSpec	| widget listClass getIndex setIndex |	aSpec getSelected ifNil:[		listClass := PluggableListMorphPlus.		getIndex := aSpec getIndex.		setIndex := aSpec setIndex.	] ifNotNil:[		listClass := PluggableListMorphByItemPlus.		getIndex := aSpec getSelected.		setIndex := aSpec setSelected.	].	widget := listClass on: aSpec model				list: aSpec list				selected: getIndex				changeSelected: setIndex				menu: aSpec menu				keystroke: aSpec keyPress.	self register: widget id: aSpec name.	widget doubleClickSelector: aSpec doubleClick.	widget dragItemSelector: aSpec dragItem.	widget dropItemSelector: aSpec dropItem.	widget wantsDropSelector: aSpec dropAccept.	widget autoDeselect: aSpec autoDeselect.	self setFrame: aSpec frame in: widget.	parent ifNotNil:[self add: widget to: parent].	panes ifNotNil:[		aSpec list ifNotNil:[panes add: aSpec list].	].	^widget! !!ChooserTool methodsFor: 'toolbuilder' stamp: 'jcg 12/12/2009 10:34'!buildChooserListWith: builder	| listSpec |	listSpec := builder pluggableListSpec new.	listSpec 		model: self;		list: #itemList; 		getIndex: #itemListIndex; 		setIndex: #itemListIndex:; 		doubleClick: #accept;		autoDeselect: false.	^listSpec! !