"Change Set:		8590NetworkTests-nice.11NetworkTests-nice.11:Cosmetic: move or remove a few temps inside closures"!!UUIDPrimitivesTest methodsFor: 'tests' stamp: 'nice 12/27/2009 04:15'!testDuplicationsKinda	| check size |	size := 5000.	check := Set new: size.	size timesRepeat: 		[ | uuid |		uuid := UUID new.		self shouldnt: [check includes: uuid].		check add: uuid].		! !!UUIDPrimitivesTest methodsFor: 'tests' stamp: 'nice 12/27/2009 04:14'!testCreationNodeBased		(UUID new asString last: 12) = (UUID new asString last: 12) ifFalse: [^self].	1000 timesRepeat:		[ | uuid |		uuid := UUID new.		self should: [((uuid at: 7) bitAnd: 16rF0) = 16r10].		self should: [((uuid at: 9) bitAnd: 16rC0) = 16r80]]! !!UUIDPrimitivesTest methodsFor: 'tests' stamp: 'nice 12/27/2009 04:15'!testOrder		100 timesRepeat:		[ | uuid1 uuid2 |		uuid1 := UUID new.		uuid2 := UUID new.		(uuid1 asString last: 12) = (uuid2 asString last: 12) ifTrue:			[self should: [uuid1 < uuid2].			self should: [uuid2 > uuid1].			self shouldnt: [uuid1 = uuid2]]]! !