"Change Set:		8759TraitsTests-nice.3TraitsTests-nice.3:remove some #or:or: #and:and: sends"!!TraitsResource methodsFor: 'as yet unclassified' stamp: 'nice 1/1/2010 21:48'!codeChangedEvent: anEvent	(anEvent isDoIt not		and: [anEvent itemClass notNil		and: [self createdClassesAndTraits includes: anEvent itemClass instanceSide]]) ifTrue: [self setDirty] ! !!TraitsResource methodsFor: 'as yet unclassified' stamp: 'nice 1/1/2010 18:00'!tearDown		SystemChangeNotifier uniqueInstance noMoreNotificationsFor: self.	self createdClassesAndTraits do: 			[:aClassOrTrait | | behaviorName | 			behaviorName := aClassOrTrait name.			Smalltalk at: behaviorName				ifPresent: [:classOrTrait | classOrTrait removeFromSystem].			ChangeSet current removeClassChanges: behaviorName].	createdClassesAndTraits := self t1: (self 						t2: (self t3: (self 										t4: (self t5: (self 														t6: (self c1: (self 																		c2: (self c3: (self c4: (self c5: (self c6: (self c7: (self c8: nil)))))))))))))! !!TraitsTestCase methodsFor: 'running' stamp: 'nice 1/1/2010 18:00'!tearDown		TraitsResource resetIfDirty.	self createdClassesAndTraits do: 			[:aClassOrTrait | | behaviorName | 			behaviorName := aClassOrTrait name.			Smalltalk at: behaviorName				ifPresent: [:classOrTrait | classOrTrait removeFromSystem].			ChangeSet current removeClassChanges: behaviorName].	createdClassesAndTraits := nil! !