"Change Set:		8817Tests-nice.44Tests-nice.44:Fast up a test with selectorsAndMethodsDo:Tests-ul.43:- code critics"!!DecompilerTests methodsFor: 'utilities' stamp: 'nice 1/16/2010 20:53'!decompileClassesSelect: aBlock		(Smalltalk classNames select: aBlock) do:		[:cn | | cls |		cls := Smalltalk at: cn.		Smalltalk garbageCollect.		 Transcript cr; show: cn.		 cls selectorsAndMethodsDo:			[:selector :meth |			(self isFailure: cls sel: selector) ifFalse:				[" to help making progress					(self						isStoredProblems: cls theNonMetaClass						sel: selector						meta: cls isMeta)					ifFalse: [ "				Transcript nextPut: $.; flush.				self checkDecompileMethod: meth]]]! !!MorphicUIBugTest methodsFor: 'as yet unclassified' stamp: 'ul 1/11/2010 07:54'!testOpenWorkspace	"self new testOpenWorkspace"	"MorphicUIBugTest run: #testOpenWorkspace"		| window myLabel foundWindow myModel |	self assert: Smalltalk isMorphic.	myLabel := 'Workspace from SUnit test' .	foundWindow := self findWindowInWorldLabeled: myLabel .	self assert: foundWindow isNil.	window := UIManager default edit: '"MorphicUIBugTest run: #openWorkspaceTest"'  label: myLabel.	window := window.	foundWindow := self findWindowInWorldLabeled: myLabel.	cases := Array with: foundWindow . "For teardown."	myModel := foundWindow submorphs detect: #isMorphicModel.	self assert: myModel model class == Workspace.	self assert: foundWindow model class == Workspace.	foundWindow delete! !