"Change Set:		8920MultilingualTests-nice.7MultilingualTests-nice.7:Use ByteArray literals"!!MultiByteFileStreamTest methodsFor: 'testing' stamp: 'nice 1/18/2010 18:08'!testBinaryUpTo	"This is a non regression test for bug http://bugs.squeak.org/view.php?id=6933"		| foo fileName |		fileName := 'foobug6933'.		foo := MultiByteFileStream forceNewFileNamed: fileName.	[foo binary.	foo nextPutAll: #[ 1 2 3 4 ]] ensure: [foo close].	foo := MultiByteFileStream oldFileNamed: fileName.	[foo binary.	self assert: (foo upTo: 3) = #[ 1 2 ]] ensure: [foo close].	FileDirectory default deleteFileNamed: fileName! !