'From Squeak3.11alpha of 13 February 2010 [latest update: #9483] on 9 March 2010 at 11:11:23 am'!!ByteArray methodsFor: 'converting' stamp: 'ar 2/20/2010 12:44'!hex	"Answer a hexa decimal representation of the receiver"	| string v index map |	map := '0123456789abcdef'.	string := String new: self size * 2. "hex"	index := 0.	1 to: self size do:[:i| 		v := self at: i.		string at: (index := index + 1) put: (map at: (v bitShift: -4) + 1). 		string at: (index := index + 1) put: (map at: (v bitAnd: 15) + 1).	].	^string! !!ByteArray methodsFor: 'initialize' stamp: 'nice 2/26/2010 00:04'!readHexFrom: aStream	"Initialize the receiver from a hexadecimal string representation"	| map v ch value |	map := '0123456789abcdefABCDEF'.	1 to: self size do:[:i|		ch := aStream next.		v := (map indexOf: ch) - 1.		((v between: 0 and: 15) or: [((v:= v - 6) between: 0 and: 15)]) ifFalse:[^self error: 'Hex digit expected'].		value := v bitShift: 4.		ch := aStream next.		v := (map indexOf: ch) - 1.		((v between: 0 and: 15) or: [((v:= v - 6) between: 0 and: 15)]) ifFalse:[^self error: 'Hex digit expected'].		value := value + v.		self at: i put: value.	].! !!ByteArray class methodsFor: 'instance creation' stamp: 'ar 2/23/2010 07:08'!readHexFrom: aString	"Create a byte array from a hexadecimal representation"	^(self new: aString size // 2) readHexFrom: aString readStream! !!WeakRegistry methodsFor: 'initialize' stamp: 'ul 2/22/2010 14:18'!installFinalizer	valueDictionary finalizer: #finalize! !