'From Squeak3.11alpha of 13 February 2010 [latest update: #9499] on 15 March 2010 at 8:32:30 am'!Error subclass: #SyntaxErrorNotification	instanceVariableNames: 'inClass code category doitFlag errorMessage location'	classVariableNames: ''	poolDictionaries: ''	category: 'Compiler-Exceptions'!!SyntaxErrorNotification methodsFor: 'accessing' stamp: 'ar 9/27/2005 19:17'!category	^category! !!SyntaxErrorNotification methodsFor: 'accessing' stamp: 'ar 9/27/2005 19:11'!doitFlag	^doitFlag! !!SyntaxErrorNotification methodsFor: 'accessing' stamp: 'ar 9/27/2005 19:10'!errorClass	^inClass! !!SyntaxErrorNotification methodsFor: 'accessing' stamp: 'ar 9/27/2005 19:10'!errorCode	^code! !!SyntaxErrorNotification methodsFor: 'accessing' stamp: 'eem 9/23/2008 14:23'!errorMessage	^errorMessage! !!SyntaxErrorNotification methodsFor: 'accessing' stamp: 'eem 9/23/2008 14:23'!location	^location! !!SyntaxErrorNotification methodsFor: 'accessing' stamp: 'ar 9/27/2005 19:14'!messageText	^ super messageText		ifNil: [messageText := code]! !!SyntaxErrorNotification methodsFor: 'accessing' stamp: 'ar 9/27/2005 19:15'!setClass: aClass category: aCategory code: codeString doitFlag: aBoolean	inClass := aClass.	category := aCategory.	code := codeString.	doitFlag := aBoolean ! !!SyntaxErrorNotification methodsFor: 'accessing' stamp: 'eem 9/23/2008 14:20'!setClass: aClass category: aCategory code: codeString doitFlag: aBoolean errorMessage: errorString location: anInteger	inClass := aClass.	category := aCategory.	code := codeString.	doitFlag := aBoolean.	errorMessage := errorString.	location := anInteger! !!SyntaxErrorNotification methodsFor: 'exceptionDescription' stamp: 'ar 9/27/2005 19:13'!defaultAction	^ToolSet debugSyntaxError: self! !!SyntaxErrorNotification class methodsFor: 'exceptionInstantiator' stamp: 'ar 9/27/2005 19:15'!inClass: aClass category: aCategory withCode: codeString doitFlag: doitFlag 	^ (self new		setClass: aClass		category: aCategory 		code: codeString		doitFlag: doitFlag) signal! !!SyntaxErrorNotification class methodsFor: 'exceptionInstantiator' stamp: 'eem 9/23/2008 14:19'!inClass: aClass category: aCategory withCode: codeString doitFlag: doitFlag errorMessage: errorString location: location	^ (self new		setClass: aClass		category: aCategory 		code: codeString		doitFlag: doitFlag		errorMessage: errorString		location: location) signal! !