e: aPage pageSize: aSize].		aBrowser _ Browser new openAsMorphEditing: nil.	aPage _ self newPageForStandardPartsBin.	aBrowser extent: (200 @ 140); setLabel: 'Browser'.	aPage addMorphBack: aBrowser.	aPartsContainer insertPage: aPage pageSize: aSize."	#(valueTiles booleanTiles arithmeticTiles) do:		[:aSym |			aPage _ self newPageForStandardPartsBin.			(self perform: aSym) do:				[:aTile | aPage addMorphBack: aTile markAsPartsDonor].			aPage fixLayout.			self coloredTilesEnabled ifFalse:				[aPage makeAllTilesGreen].			aPartsContainer insertPage: aPage pageSize: aSize]."	aPartsContainer goToPage: 1.	aWindow _ SystemBookWindow new.	aWindow setLabel: 'Building Blocks'.	aWindow color: Color blue veryMuchLighter.	aWindow addMorph: aPartsContainer frame: (0@0 corner: 0.3@0.5).	aWindow extent: (aSize + (18 @ 38)).	aWindow borderColor: Color blue lighter; borderWidth: 1.	^ aWindow! !!TinyPaint methodsFor: 'events' stamp: 'sw 5/11/1998 14:02'!handlesMouseDown: evt	^ self inPartsBin not! !