gned char*		curdst = &dst[1];		unsigned char		thisChar;						thisChar = *(const unsigned char*)src++;		while ( thisChar !!= ''\0'' ) 		{			unsigned char	nextChar;						// use nextChar so we don''t overwrite what we are about to read			nextChar = *(const unsigned char*)src++;			*curdst++ = thisChar;			thisChar = nextChar;						if ( ++length >= 255 )				break;		}	}	else if ( src !!= NULL )	{		unsigned char*		curdst = &dst[1];		short 				overflow = 255;		// count down so test it loop is faster		register char		temp;			// Can''t do the K&R C thing of while (*s++ = *t++) because it will copy trailing zero		// which might overrun pascal buffer.  Instead we use a temp variable.		while ( (temp = *src++) !!= 0 ) 		{			*(char*)curdst++ = temp;							if ( --overflow <= 0 )				break;		}		length = 255 - overflow;	}	dst[0] = length;}#endif'! !!InterpreterSupportCode class methodsFor: 'source files' stamp: 'JMM 8/16/2000 13:24'!squeakGSMCodecPluginFile	^ '/* * Copyright 1992 by Jutta Degener and Carsten Bormann, Technische * Universitaet Berlin.  See the accompanying file "COPYRIGHT" for * details.  THERE IS ABSOLUTELY NO WARRANTY FOR THIS SOFTWARE. *//* This file was created by concatenating a number of separate header   and source files from Jutta Degener and Carsten Bormann implementation,   patch level 10. This was done to simplify Squeak source code maintenance. */#include <assert.h>#include <stdio.h>#include <string.h>/****** begin "gsm.h" *****/#ifdef __cplusplus#	define	NeedFunctionPrototypes	1#endif#if __STDC__#	define	NeedFunctionPrototypes	1#endif#ifdef _NO_PROTO#	undef	NeedFunctionPrototypes#endif#ifdef NeedFunctionPrototypes#   include	<stdio.h>		/* for FILE * 	*/#endif#undef GSM_P#if NeedFunctionPrototypes#	define	GSM_P( protos )	protos#else#	define  GSM_P( protos )	( /* protos */ )#endif/* *	Interface */typedef struct gsm_state * 	gsm;typedef short		   	gsm_signal;		/* signed 16 b