nsform "or: [formerOwner isNil]")		ifTrue:[myPos]		ifFalse:[((aMorph world ifNil: [self worldUnderCursor]) 							globalPointToLocal: myPos) truncated].						"fix grabbing new morph in embedded world"	(formerOwner == nil		or: [aMorph keepsTransform			or: [(trans _ formerOwner transformFromOutermostWorld) isPureTranslation]]) ifTrue: [		aMorph position: myPos + (morphPos - localPt).		morphToGrab _ aMorph.		addedFlexAtGrab _ false.	] ifFalse: ["Morph was in a flexed environment.  Flex it in the hand."		morphToGrab _ aMorph addFlexShell transform: trans asMorphicTransform.		aMorph position: morphPos.  "revert offset from addFlexShell"		newT _ trans withOffset: (trans offset + (trans localPointToGlobal: localPt) - myPos).		morphToGrab position: (morphToGrab localPointToGlobal: morphToGrab position).		morphToGrab transform: newT.		addedFlexAtGrab _ true.	].	(morphToGrab isKindOf: PhraseTileMorph) ifTrue:		[morphToGrab position: Sensor cursorPoint].	gridOn ifTrue: [morphToGrab position: (morphToGrab position grid: grid)].	self addMorphBack: morphToGrab! !!HandMorph methodsFor: 'world menu' stamp: 'sw 8/29/2000 14:25'!addUndoItemsTo: aWorldMenu	"Add undo-related items to the given menu.  Will add zero, one or two items, depending on the settings of the #useUndo and #infiniteUndo preferences"	Preferences useUndo ifFalse: [^ self].	Preferences infiniteUndo		ifFalse:			[aWorldMenu addUpdating: #undoOrRedoMenuWording target: Command action: #undoOrRedoCommand]		ifTrue:			[aWorldMenu addUpdating: #undoMenuWording target: Command  action: #undoLastCommand.			aWorldMenu addUpdating: #redoMenuWording target: Command action: #redoNextCommand.			self flag: #deferred.  "The following feature to be unblocked in due course"			"aWorldMenu add: 'undo to...' target: Command action: #undoTo"].	aWorldMenu addLine! !!HandMorph methodsFor: 'object fileIn' stamp: 'sw 8/29/2000 14:26'!convertbosfceekmmmdccffuleatdccttggrtllmhssffa0: varDict bosfceekmmmdc