'From Squeak2.9alpha of 12 June 2000 [latest update: #2696] on 24 September 2000 at 7:08:17 pm'!!Date methodsFor: 'printing' stamp: 'di 9/22/2000 12:47'!storeOn: aStream	aStream print: self printString; nextPutAll: ' asDate'! !!PDARecurringEvent methodsFor: 'as yet unclassified' stamp: 'di 9/22/2000 12:57'!matchesDate: aDate	(firstDate == nil or: [firstDate > aDate]) ifTrue: [^ false].	(lastDate ~~ nil and: [lastDate < aDate]) ifTrue: [^ false].	recurrence == #eachDay ifTrue:		[^ true].	recurrence == #dayOfWeek ifTrue:		[^ aDate weekday = firstDate weekday].	recurrence == #dayOfMonth ifTrue:		[^ aDate dayOfMonth = firstDate dayOfMonth].	recurrence == #dateOfYear ifTrue:		[^ aDate monthIndex = firstDate monthIndex			and: [aDate dayOfMonth = firstDate dayOfMonth]].	recurrence == #nthWeekdayOfMonth ifTrue:		[^ aDate weekday = firstDate weekday			and: [(aDate dayOfMonth - 1 // 7) = (firstDate dayOfMonth - 1 // 7)]].	recurrence == #nthWeekdayOfMonthEachYear ifTrue:		[^ aDate monthIndex = firstDate monthIndex			and: [aDate weekday = firstDate weekday			and: [(aDate dayOfMonth - 1 // 7) = (firstDate dayOfMonth - 1 // 7)]]].! !!PDARecurringEvent methodsFor: 'as yet unclassified' stamp: 'di 9/24/2000 21:38'!recurrence: rSymbol	(#(eachDay dayOfWeek dayOfMonth dateOfYear nthWeekdayOfMonth nthWeekdayOfMonthEachYear) includes: rSymbol)		ifFalse: [^ self error: 'unrecognized recurrence symbol: , rSymbol'].	recurrence _ rSymbol! !!Time methodsFor: 'printing' stamp: 'di 9/22/2000 12:46'!storeOn: aStream	aStream print: self printString; nextPutAll: ' asTime'! !