* no samples available */	/* copy samples into the client''s buffer */	src = &recBuf->samples[0] + recBuf->readIndex;	srcEnd = &recBuf->samples[RECORD_BUFFER_SIZE];	if (recBuf->bytesPerSample == 1) {		while ((src < srcEnd) && (nextBuf < bufEnd)) {			/* convert 8-bit sample to 16-bit sample */			*nextBuf++ = (*src++) - 128;  /* convert from [0-255] to [-128-127] */			*nextBuf++ = 0;  /* low-order byte is zero */		}	} else {		while ((src < srcEnd) && (nextBuf < bufEnd)) {			*nextBuf++ = *src++;		}	}	recBuf->readIndex = src - &recBuf->samples[0];  /* update read index */	/* return the number of slices copied */	bytesCopied = (int) nextBuf - (buf + (startSliceIndex * bytesPerSlice));	return bytesCopied / bytesPerSlice;}//Nov 6th 2000void snd_Volume(double *left, double *right) {	double temp;	SndCommand cmd;    long   results;	OSErr  err;		*left = 1.0;	*right = 1.0;	cmd.cmd = getVolumeCmd;	cmd.param1 = 0;	cmd.param2 = (long) &results;	err = -1;	if (chan !!= null)	    err = SndDoImmediate(chan,&cmd);	    	if (err !!= noErr) {	   err = GetDefaultOutputVolume(&results);	}	if (err !!= noErr) 	    return;	temp  = (results & 0xFFFF);	*left = temp/256.0;		temp = (results >> 16);	*right = temp/256.0;}void snd_SetVolume(double left, double right) {	unsigned long tempLeft,tempRight;	SndCommand cmd;	OSErr  err;		tempLeft = left*256.0 + 0.5;	tempLeft = tempLeft & 0xFFFF;	tempRight = right*256.0 + 0.5;	tempRight= tempRight & 0xFFFF;	cmd.cmd = volumeCmd;	cmd.param1 = 0;	cmd.param2 = (tempRight << 16) | tempLeft;	err = -1;	if (chan !!= null)	    err= SndDoImmediate(chan,&cmd);	if (err !!= noErr) {	   err = SetDefaultOutputVolume(cmd.param2);	}}/*	File:		CarbonSndPlayDB.c		Description:Routines demonstrating how to create a function that works				much like the original SndPlayDoubleBuffer but is Carbon compatible				(which SndPlayDoubleBuffer isn''t).	Author:		MC	Copyright: 	 Copyright 1999-2000 Apple Comp