                anErr = AECountItems(&(reply.selection), &count);                // Set up index for file list                if (anErr == noErr)                {                    long index;                                        for (index = 1; index <= 1; index++)                    {                        AEKeyword   theKeyword;                        DescType    actualType;                        Size        actualSize;                                                // Get a pointer to selected file                        anErr = AEGetNthPtr(&(reply.selection), index,                                            typeFSS, &theKeyword,                                            &actualType,documentFSSpec,                                            sizeof(FSSpec),                                            &actualSize);                     }                }                //  Dispose of NavReplyRecord, resources, descriptors                anErr = NavDisposeReply(&reply);            }            if (typeList !!= NULL)            {                ReleaseResource( (Handle)typeList);            }            (void) AEDisposeDesc(&defaultLocation);        }    }    DisposeNavEventUPP(eventProc);    DisposeNavObjectFilterUPP(filterProc);    return anErr;}pascal void findImageEventProc(NavEventCallbackMessage callBackSelector,                         NavCBRecPtr callBackParms,                         NavCallBackUserData callBackUD){   // WindowPtr window =    //                 (WindowPtr)callBackParms->eventData.event->message;    switch (callBackSelector)    {        case kNavCBEvent:            switch (((callBackParms->eventData)                    .eventDataParms).event->what)            {                case updateEvt:                   // MyHandleUpdateEvent(window,                     //    (EventRecord*)callBackParms->eventData.event);                    break;            }            break;    }}pascal B