EOF);		if (file->mode.io_mode == __read)		/* mm 980430 */		return 0;							/* mm 980430 */		if (file->state.io_state >= __rereading)		file->state.io_state = __reading;		if (file->state.io_state == __reading)		file->buffer_len = 0;		if (file->state.io_state !!= __writing)	{		file->state.io_state = __neutral;  /* mm 970905 */		return(0);	}	#ifndef _No_Disk_File_OS_Support	if (file->mode.file_kind !!= __disk_file || (position = ftell(file)) < 0)		position = 0;#else	position = 0;#endif		if (__flush_buffer(file, NULL))	{		set_error(file);		return(EOF);	}		file->state.io_state = __neutral;	file->position       = position;	file->buffer_len     = 0;		pb.ioParam.ioRefNum = file->handle;    error = PBFlushFileSync(&pb); 	return(0);}#endif'! !!InterpreterSupportCode class methodsFor: 'source files' stamp: 'JMM 2/18/2001 20:46'!macWindowFile	^ '#include "sq.h"#include <AppleEvents.h>#include <Dialogs.h>#include <Devices.h>#include <Files.h>#include <Fonts.h>#include <Gestalt.h>#include <LowMem.h>#include <Memory.h>#include <Menus.h>#include <OSUtils.h>#include <Power.h>#include <QuickDraw.h>#include <Scrap.h>#include <Strings.h>#include <Timer.h>#include <ToolUtils.h>#include <Windows.h>#ifndef __MPW__#include <profiler.h>#endif#include <sound.h>#include <Math64.h>#ifndef __MPW__#include <cstddef>#endif#include <processes.h>#include <OpenTransport.h>#include <Threads.h>#include <DriverServices.h>#include <stddef.h>/*** Compilation Options:**	define PLUGIN		to compile code for Netscape or IE Plug-in*	define MAKE_PROFILE	to compile code for profiling****///#define PLUGIN//#define MAKE_PROFILE//#define IHAVENOHEAD//Aug 7th 2000,JMM Added logic for interrupt driven dispatching//Sept 1st 2000, JMM fix problem with modifier information being passed back incorrectly.//Sept 1st 2000, JMM use floating point for time versus 64bit math (faster!!)//Sept 1st 2000, JMM watch mouse movement foregroun