s.");	}#ifdef MAKE_PROFILE	ProfilerInit(collectDetailed, bestTimeBase, 1000, 50);	ProfilerSetStatus(false);	ProfilerClear();#endif	/* compute the desired memory allocation */#ifdef JITTER	reservedMemory = 1000000;#else	reservedMemory = 500000;#endif	if (RunningOnCarbonX())	    availableMemory = 100*1024*1024 - reservedMemory;	else     	availableMemory = MaxBlock() - reservedMemory;		/******	  Note: This is platform-specific. On the Mac, the user specifies the desired	    memory partition for each application using the Finder''s Get Info command.	    MaxBlock() returns the amount of memory in the partition minus space for	    the code segment and other resources. On other platforms, the desired heap	    size would be specified in other ways (e.g, via a command line argument).	    The maximum size of the object heap is fixed at at startup. If you run low	    on space, you must save the image and restart with more memory.	  Note: Some memory must be reserved for Mac toolbox calls, sound buffers, etc.	    A 30K reserve is too little. 40K allows Squeak to run but crashes if the	    console is opened. 50K allows the console to be opened (with and w/o the	    profiler). I added another 30K to provide for sound buffers and reliability.	    (Note: Later discovered that sound output failed if SoundManager was not	    preloaded unless there is about 100K reserved. Added 50K to that.)	    	    JMM Note changed to 500k for Open Transport support on 68K machines	******/	/* uncomment the following when using the C transcript window for debugging: */	//printf("Move this window, then hit CR\n"); getchar();	/* read the image file and allocate memory for Squeak heap */	f = sqImageFileOpen(imageName, "rb");	while (f == NULL) {	    //Failure attempt to ask the user to find the image file	    	    FSSpec vmfsSpec,imageFsSpec;	    WDPBRec wdPB;	    	    err =  FSpLocationFromFullPath(vmPathSize(),vmPath,&vmfsSpec);	    if (err) 	        ioExit();		err = squeakFindImage(&vmfsSpec,&imageFsSpec);	    if (err) 	        ioExit();	    CopyPascalStringToC(imageFsSpec.name,shortImageName);        StoreFullPathForLocalNameInto(shortImageName, imageName, IMAGE_NAME_SIZE, imageFsSpec.vRefNum, imageFsSpec.parID);			/* make the image or document directory the working directory */    	wdPB.ioNamePtr = NULL;    	wdPB.ioVRefNum = imageFsSpec.vRefNum;    	wdPB.ioWDDirID = imageFsSpec.parID;    	PBHSetVolSync(&wdPB);    	f = sqImageFileOpen(imageName, "rb"); 	}		readImageFromFileHeapSizeStartingAt(f, availableMemory, calculateStartLocationForImage());	sqImageFileClose(f);    ioInitSecurity();    #ifndef IHAVENOHEAD	SetWindowTitle(shortImageName);	ioSetFullScreen(fullScreenFlag);#endif#if (!!(defined JITTER) && defined(__MPW__))	atexit(SqueakTerminate);#endif#if TARGET_CPU_PPC    if( Gestalt( gestaltThreadMgrAttr, &threadGestaltInfo) == noErr &&        threadGestaltInfo & (1<<gestaltThreadMgrPresent) &&        ((Ptr) NewThread !!= (Ptr)kUnresolvedCFragSymbolAddress)) {        gThreadManager = true;        err = createNewThread();        if (err == noErr) {            while(true)  {                ioProcessEvents();        		YieldToAnyThread();            }            return;        }    }        #endif    gThreadManager = false;    /* run Squeak */    squeakThread(0);}#endifOSErr createNewThread() {#if TARGET_CPU_PPC    gSqueakThreadUPP = NewThreadEntryUPP(squeakThread); //We should dispose of someday#else    gSqueakThreadUPP = (ThreadEntryProcPtr) NewRoutineDescriptor((ProcPtr)(squeakThread), uppThreadEntryProcInfo, GetCurrentArchitecture()); #endif	return NewThread( kCooperativeThread, gSqueakThreadUPP, nil, 80*1024, kCreateIfNeeded, 0L, &gSqueakThread);}static pascal void* squeakThread(void *threadParm) {	/* run Squeak */#	ifdef JITTER	j_interpret();#	else	interpret();#	endif}void SqueakTerminate() {#ifdef PLUGIN	ExitCleanup();#else	ioShutdownAllModules();#endif}WindowPtr getSTWindow(void) {    return stWindow;}int setMessageHook(eventMessageHook theHook) {    messageHook = theHook;}int setPostMessageHook(eventMessageHook theHook) {    postMessageHook = theHook;}#if !!TARGET_API_MAC_CARBON////	CopyPascalStringToC converts the source pascal string to a destination//	C string as it copies. //void CopyPascalStringToC(ConstStr255Param src, char* dst){	if ( src !!= NULL )	{		short   length  = *src++;			while ( length > 0 ) 		{			*dst++ = *(char*)src++;			--length;		}	}	*dst = ''\0'';}////	CopyCStringToPascal converts the source C string to a destination//	pascal string as it copies. The dest string will//	be truncated to fit into an Str255 if necessary.//  If the C String pointer is NULL, the pascal string''s length is set to zero//void CopyCStringToPascal(const char* src, Str255 dst){	short 	length  = 0;		// handle case of overlapping strings	if ( (void*)src == (void*)dst )	{		unsigned char*		curdst = &dst[1];		unsigned char		thisChar;						thisChar = *(const unsigned char*)src++;		while ( thisChar !!= ''\0'' ) 		{			unsigned char	nextChar;						// use nextChar so we don''t overwrite what we are about to read			nextChar = *(const unsigned char*)src++;			*curdst++ = thisChar;			thisChar = nextChar;						if ( ++length >= 255 )				break;		}	}	else if ( src !!= NULL )	{		unsigned char*		curdst = &dst[1];		short 				overflow = 255;		// count down so test it loop is faster		register char		temp;			// Can''t do the K&R C thing of while (*s++ = *t++) because it will copy trailing zero		// which might overrun pascal buffer.  Instead we use a temp variable.		while ( (temp = *src++) !!= 0 ) 		{			*(char*)curdst++ = temp;							if ( --overflow <= 0 )				break;		}		length = 255 - overflow;	}	dst[0] = length;}#endif#define rectWidth(aRect) ((aRect).right - (aRect).left)#define rectHeight(aRect) ((aRect).bottom - (aRect).top)#define MinWindowWidth(foo) 72*3#define MinWindowHeight(foo) 72*3#define max(X, Y) ( ((X)>(Y)) ? (X) : (Y) )#define min(X, Y) (  ((X)>(Y)) ? (Y) : (X) )#define pin(VALUE, MIN, MAX) ( ((VALUE) < (MIN)) ? (MIN) : ( ((VALUE) > (MAX)) ? (MAX) : (VALUE) ) )void DoZoomWindow (EventRecord* theEvent, WindowPtr theWindow, short zoomDir, short hMax, short vMax){	Rect				zoomRect,windRect,globalPortRect, dGDRect;	GDHandle			dominantGDevice;   	if (TrackBox(theWindow, theEvent->where, zoomDir)) {		SetPortWindowPort(theWindow);		GetPortBounds(GetWindowPort(stWindow),&windRect);		EraseRect(&windRect);	// recommended for cosmetic reasons		if (zoomDir == inZoomOut) {			/*			 *	ZoomWindow() is a good basic tool, but it doesn''t do everything necessary to			 *	implement a good human interface when zooming. In fact it''s not even close for			 *	more high-end hardware configurations. We must help it along by calculating an			 *	appropriate window size and location any time a window zooms out.			 */            dominantGDevice = getDominateDevice(theWindow,&windRect);			/*			 *	At this point, we know the dimensions of the window we''re zooming, and we know			 *	what screen we''re going to put it on. To be more specific, however, we need a			 *	rectangle which defines the maximum dimensions of the resized window''s contents.			 *	This rectangle accounts for the thickness of the window frame, the menu bar, and			 *	one or two pixels around the edges for cosmetic compatibility with ZoomWindow().			 */            getDominateGDeviceRect(dominantGDevice,&dGDRect,false);            			GetPortBounds(GetWindowPort(theWindow),&globalPortRect);			LocalToGlobal(&(((Point *) &(globalPortRect))[0]));		// calculate the window''s portRect			LocalToGlobal(&(((Point *) &(globalPortRect))[1]));		// in global coordinates			// account for the window frame and inset it a few pixels			dGDRect.left	+= 2 + globalPortRect.left - windRect.left;			dGDRect.top		+= 2 + globalPortRect.top - windRect.top;			dGDRect.right	-= 1 + windRect.right - globalPortRect.right;			dGDRect.bottom	-= 1 + windRect.bottom - globalPortRect.bottom;			/*			 *	Now we know exactly what our limits are, and since there are input parameters			 *	specifying the dimensions we''d like to see, we can move and resize the zoom			 *	state rectangle for the best possible results. We have three goals in this:			 *	1. Display the window entirely visible on a single device.			 *	2. Resize the window to best represent the dimensions of the document itself.			 *	3. Move the window as short a distance as possible to achieve #1 and #2.			 */			GetWindowStandardState(theWindow, &zoomRect);			/*			 *	Initially set the zoom rectangle to the size requested by the input parameters,			 *	although not smaller than a minimum size. We do this without moving the origin.			 */			zoomRect.right = (zoomRect.left = globalPortRect.left) +									max(hMax, MinWindowWidth(theWindow));			zoomRect.bottom = (zoomRect.top = globalPortRect.top) +									max(vMax, MinWindowHeight(theWindow));			// Shift the entire rectangle if necessary to bring its origin inside dGDRect.			OffsetRect(&zoomRect,						max(dGDRect.left - zoomRect.left, 0),						max(dGDRect.top - zoomRect.top, 0));			/*			 *	Shift the rectangle up and/or to the left if necessary to accomodate the view,			 *	and if it is possible to do so. The rectangle may not be moved such that its			 *	origin would fall outside of dGDRect.			 */			OffsetRect(&zoomRect,						-pin(zoomRect.right - dGDRect.right, 0, zoomRect.left - dGDRect.left),						-pin(zoomRect.bottom - dGDRect.bottom, 0, zoomRect.top - dGDRect.top));			// Clip expansion to dGDRect, in case view is larger than dGDRect.			zoomRect.right = min(zoomRect.right, dGDRect.right);			zoomRect.bottom = min(zoomRect.bottom, dGDRect.bottom);			SetWindowStandardState(theWindow, &zoomRect);		}		ZoomWindow(theWindow, zoomDir, false);		// all it needed was a brain transplant	}}GDHandle getDominateDevice( WindowPtr theWindow,Rect *windRect) {	GDHandle			nthDevice, dominantGDevice;	long				sectArea, greatestArea;    long                quickDrawAttributes;	Rect				theSect;    RgnHandle           windowRegion;    #if TARGET_API_MAC_CARBON			windowRegion = NewRgn();			GetWindowRegion(theWindow,kWindowStructureRgn,windowRegion);			GetRegionBounds(windowRegion,windRect);#else			*windRect = (**((WindowPeek) theWindow)->strucRgn).rgnBBox;#endif			dominantGDevice = nil;    	    if (!! Gestalt(gestaltQuickdrawFeatures, &quickDrawAttributes) &&     	        (quickDrawAttributes & (1<<gestaltHasColor))) {				/*				 *	Color QuickDraw implies the possibility of multiple monitors. This is where				 *	zooming becomes more interesting. One should zoom onto the monitor containing				 *	the greatest portion of the window. This requires walking the gDevice list.				 */				nthDevice = GetDeviceList();				greatestArea = 0;				while (nthDevice !!= nil) {					if (TestDeviceAttribute(nthDevice, screenDevice)) {						if (TestDeviceAttribute(nthDevice, screenActive)) {							SectRect(windRect, &(**nthDevice).gdRect, &theSect);							sectArea = (long) rectWidth(theSect) * (long) rectHeight(theSect);							if (sectArea > greatestArea) {								greatestArea = sectArea;		// save the greatest intersection								dominantGDevice = nthDevice;	// and which device it belongs to							}						}					}					nthDevice = GetNextDevice(nthDevice);				}			}    return dominantGDevice;}void getDominateGDeviceRect(GDHandle dominantGDevice,Rect *dGDRect,Boolean forgetMenuBar) {    BitMap              bmap;	if (dominantGDevice !!= nil) {			*dGDRect = (**dominantGDevice).gdRect;			if (dominantGDevice == GetMainDevice())		// account for menu bar on main device				if (!!forgetMenuBar) 				        dGDRect->top += GetMBarHeight();		}		else {			GetQDGlobalsScreenBits(&bmap);			*dGDRect = bmap.bounds;				// if no gDevice, use default monitor			if (!!forgetMenuBar)			    dGDRect->top += GetMBarHeight();		}}'! !