kgroundColorForMvcProject.	colorPattern add: Project someInstance backgroundColorForMorphicProject.	colorPattern add: #orange.	Utilities windowFromMenu: self openMenu target: self title: 'Openers'		colorPattern: colorPattern! !!ScreenController methodsFor: 'nested menus' stamp: 'di 11/25/1998 12:23'!openMenu	^ SelectionMenu labelList:		#(	'keep this menu up'			'browser'			'workspace'			'file list'			'transcript'			'selector finder'			'project (mvc)'			'project (morphic)'			'project (construction)'			)		lines: #(1 6)		selections: #(durableOpenMenuopenBrowser openWorkspace openFileList openTranscript openSelectorBrowseropenProject  openMorphicProject  openConstructionProject )"ScreenController  new openMenu startUp"! !!SequenceableCollection methodsFor: 'converting' stamp: 'djm 11/20/1998 05:44'!asStringWithCr	"Convert to a string with returns between items.  Elements areusually strings.	 Useful for labels for PopUpMenus."	| labelStream |	labelStream _ WriteStream on: (String new: 200).	self do: [:each |		(each isKindOf: String)			ifTrue: [labelStream nextPutAll: each; cr]			ifFalse: [each printOn: labelStream. labelStream cr]].	self size > 0 ifTrue: [labelStream skip: -1].	^ labelStream contents! !!SystemDictionary methodsFor: 'sources, change log' stamp: 'di 11/25/1998 14:20'!version	"Answer the version of this release."	^ 'Squeak 2.3 beta of Nov 25, 1998'! !!SystemWindow methodsFor: 'top window' stamp: 'di 11/25/1998 15:15'!activate	"Bring me to the front and make me able to respond to mouse and keyboard"	| oldTop outerMorph |	outerMorph _ self topRendererOrSelf.	outerMorph owner ifNil: [^ self "avoid spurious activate when drop in trash"].	oldTop _ TopWindow.	TopWindow _ self.	oldTop ifNotNil: [oldTop passivate].	outerMorph owner firstSubmorph == outerMorph		ifFalse: ["Bring me (with any flex) to the top if not already"				outerMorph owner addMorphFront: outerMorph].	self submorphsDo: [:m | m unlock].	self setStripeColorsFrom: self paneColor.	self isCollapsed ifFalse:		[model modelWakeUp.		self positionSubmorphs]! !