r	('¶'			reshapeBackground  		'Reshape')	"		spacer		('§'			showDesignationsOfObjects 	'Show designations')		spacer		('>'			goToNextCardInStack			'Next card')		spacer		( '»'		goToLastCardOfStack			'Final card')		spacer		('+'			insertCard					'Add a new card after this one')		variableSpacer		('◊'			fewerPageControls			'Fewer controls(if shift key pressed,deletes controls)'))! !!StackMorph methodsFor: 'page controls' stamp: 'sw 10/30/2000 10:09'!shortControlSpecs	"Answer specficiations for the shorter form of stack controls"	^ #(		spacer		variableSpacer		( '<'		goToPreviousCardInStack		'Previous card')		spacer		('•'		invokeBookMenu 			'Click here to get a menu for this stack.')		spacer		('>'		goToNextCardInStack			'Next card')		variableSpacer		('◊'	showMoreControls				'More controls(if shift key pressed,deletes controls)'))! !!String methodsFor: 'converting' stamp: 'sw 9/1/2000 10:39'!asCharacter	"Answer the receiver's first character, or '•' if none.  Idiosyncratic, provisional."	^ self size > 0 ifTrue: [self first] ifFalse: [$•]! !!String methodsFor: 'converting' stamp: 'tk 4/13/2002 09:13'!asUnHtml	"Strip out all Html stuff (commands in angle brackets <>) and convertthe characters &<> back to their real value.  Leave actual cr and tab asthey were in text."	| in out char rest did |	in _ ReadStream on: self.	out _ WriteStream on: (String new: self size).	[in atEnd] whileFalse:		[in peek = $<			ifTrue: [in unCommand] 	"Absorb <...><...>"			ifFalse: [(char _ in next) = $&						ifTrue: [rest _ in upTo: $;.								did _ out position.								rest = 'lt' ifTrue: [out nextPut: $<].								rest = 'gt' ifTrue: [out nextPut: $>].								rest = 'amp' ifTrue: [out nextPut: $&].								rest = 'deg' ifTrue: [out nextPut: $°].								rest = 'quot' ifTrue: [out nextPut: $"].								did = out position ifTrue: [									self error: 'unknown encoded HTML char'.									"Please add it to this method"]]						ifFalse: [out nextPut: char]].		].	^ out contents! !!SyntaxError methodsFor: 'initialization' stamp: 'tk 5/6/1999 13:28'!setClass: aClass code: aString debugger: aDebugger doitFlag: flag	| types printables badChar |	class _ aClass.	debugger _ aDebugger.	selector _ aClass parserClass new parseSelector: aString.	types _ Scanner classPool at: #TypeTable.	"dictionary"	printables _ '!!@#$%&*-_=+<>{}?/\,•£¢§¶ªº–—“‘”’…ÚæÚ¯˘¿«»`~`' asSet.	badChar _ aString detect: [:aChar | (types at: aChar asciiValue) == #xBinary and: [			(printables includes: aChar) not]] ifNone: [nil].	contents _ badChar 		ifNil: [aString]		ifNotNil: ['<<<This string contains a character (ascii value ', 			badChar asciiValue printString,			') that is not normally used in code>>> ', aString].	category ifNil: [category _ aClass organization categoryOfElement: selector].	category ifNil: [category _ ClassOrganizer default].	doitFlag _ flag! !!SyntaxMorph methodsFor: 'node to morph' stamp: 'RAA 2/26/2001 14:22'!alanBinaryPostRcvr: aNode key: key args: args	| nodeWithNilReceiver row |"==Repeat for collection [ collect ( from foo. blah blah foo blah) ]Repeat for 1 to 50  [ do  ( from i. blah blab i blah  )  ]=="	nodeWithNilReceiver _ aNode copy receiver: nil.	(row _ self addRow: #keyword2 on: nodeWithNilReceiver)		borderWidth: 1;		parseNode: (nodeWithNilReceiver as: MessageNode);		borderColor: row stdBorderColor.	row addToken: key asString		type: #binary		on: (SelectorNode new key: key asString code: nil "fill this in?").	args first asMorphicSyntaxIn: row.! !!SyntaxUpdatingStringMorph methodsFor: 'drawing' stamp: 'tk 1/31/2002 09:53'!drawOn: aCanvas	| tempForm strm where chars wid spaceWidth putLigature topOfLigature sizeOfLigature colorOfLigature dots charZero canvas f |	tempForm _ Form extent: self extent depth: aCanvas depth.	canvas _ tempForm getCanvas.	f _ self fontToUse.	spaceWidth _ f widthOf: Character space.	strm _ ReadStream on: contents.	charZero _ Character value: 0.	"a marker for center dot •"	where _ 0@0.	topOfLigature _ self height // 2 - 1.	sizeOfLigature _ (spaceWidth-2)@(spaceWidth-2).	colorOfLigature _ Color black alpha: 0.45	"veryLightGray".	dots _ OrderedCollection new.	putLigature _ [		dots add: ((where x + 1) @ topOfLigature extent: sizeOfLigature).		where _ where + (spaceWidth@0)].	strm peek = charZero ifTrue: [		strm next.		putLigature value].	[strm peek = charZero] whileTrue: [strm next].	[strm atEnd] whileFalse: [		chars _ strm upTo: charZero.		wid _ f widthOfString: chars.		canvas drawString: chars at: where.		where _ where + (wid@0).		strm atEnd ifFalse: [putLigature value.			[strm peek = charZero] whileTrue: [strm next]].	].	aCanvas paintImage: tempForm at: self topLeft.	dots do: [ :each |		aCanvas 			fillRectangle: (each translateBy: self topLeft) 			fillStyle: colorOfLigature.	].! !!SystemNavigation methodsFor: 'browse' stamp: 'sd 4/15/2003 22:46'!browseMethodsWhoseNamesContain: aString	"Launch a tool which shows all methods whose names contain the given string; case-insensitive.•	1/16/1996 sw, at the dawn of Squeak: this was the classic implementation that provided the underpinning for the 'method names containing it' (cmd-shift-W) feature that has always been in Squeak -- the feature that later inspired the MethodFinder (aka SelectorBrowser).•	sw 7/27/2001:	Switched to showing a MessageNames tool rather than a message-list browser, if in Morphic."	| aList |	Smalltalk isMorphic		ifFalse:			[aList _ Symbol selectorsContaining: aString.			aList size > 0 ifTrue:				[self browseAllImplementorsOfList: aList asSortedCollection title: 'Methods whose names contain ''', aString, '''']]		ifTrue:			[(MessageNames methodBrowserSearchingFor: aString) openInWorld]	! !!TextDiffBuilder methodsFor: 'creating patches' stamp: 'RAA 5/2/2001 23:35'!buildPatchSequence	"@@ TODO: Das funktioniert noch nicht für n-m matches"	matches := TwoLevelDictionary new.	self buildReferenceMap.	runs := self processDiagonals.	self validateRuns: runs.	"There may be things which have just been moved around. Find those."	shifted := self detectShiftedRuns.	self processShiftedRuns.	"Now generate a patch sequence"	patchSequence := self generatePatchSequence.	^patchSequence! !!TextMorph methodsFor: 'accessing' stamp: 'dgd 2/21/2003 22:27'!getFirstCharacter	"obtain the first character from the receiver if it is empty, return a  	black dot"	| aString |	^ (aString _ text string) isEmpty		ifTrue: ['•']		ifFalse: [aString first] ! !!TextMorph methodsFor: 'scripting access' stamp: 'sw 9/15/2000 06:14'!getAllButFirstCharacter	"Obtain all but the first character from the receiver; if that would be empty, return a black dot"	| aString |	^ (aString _ text string) size > 1 ifTrue: [aString copyFrom: 2 to: aString size] ifFalse: ['•']! !!TextMorph methodsFor: 'scripting access' stamp: 'dgd 2/21/2003 22:31'!setAllButFirstCharacter: source 	"Set all but the first char of the receiver to the source"	| aChar chars |	aChar _ source asCharacter.	(chars _ self getCharacters) isEmpty		ifTrue: [self newContents: '•' , source asString]		ifFalse: [chars first = aChar				ifFalse: [""					self						newContents: (String								streamContents: [:aStream | 									aStream nextPut: chars first.									aStream nextPutAll: source])]] ! !!EToyVocabulary methodsFor: 'language translations' stamp: 'sw 4/15/2003 23:45'!addGermanVocabulary	"Add a German etoy vocabulary"	self translateMethodInterfaceWordings: #((append:					'hänge an'				'Fügt ein Objekt in diesen Behälter ein')(prepend:					'hänge davor'				'Fügt ein Objekt in diesen Behälter ein')(beep:						'mache Geräusch'  				'Macht das angegebene Geräusch')(bounce:					'pralle ab'					'Läßt das Objekt vom Rand seines Behälters abprallen und spielt das angegebene Geräusch, wenn es außerhalb ist')(cameraPoint 				'Kamerapunkt'				'Der Kamerapunkt des Objektes') (clear 						'lösc