	Smalltalk isMorphic ifTrue:
		[world reformulateUpdatingMenus.
		world presenter positionStandardPlayer.		self assureMainDockingBarPresenceMatchesPreference].

	WorldState addDeferredUIMessage: [self startResourceLoading].! !!Project methodsFor: 'docking bars support' stamp: 'dgd 9/20/2004 16:29'!assureMainDockingBarPresenceMatchesPreference	"Syncronize the state of the receiver's dockings with the  	preference"	(self showWorldMainDockingBar)		ifTrue: [self createOrUpdateMainDockingBar]		ifFalse: [self removeMainDockingBar]! !!Project methodsFor: 'docking bars support' stamp: 'dgd 9/6/2004 18:47'!createOrUpdateMainDockingBar	"Private - create a new main docking bar or update the current one"	| w mainDockingBars |	w := self world.	mainDockingBars := w mainDockingBars.	mainDockingBars isEmpty		ifTrue: ["no docking bar, just create a new one"			TheWorldMainDockingBar instance createDockingBar openInWorld: w.			^ self].	""	"update if nedeed"	mainDockingBars		do: [:each | TheWorldMainDockingBar instance updateIfNeeded: each]! !!Project methodsFor: 'docking bars support' stamp: 'dgd 9/6/2004 18:46'!removeMainDockingBar	"Remove the receiver's main docking bars"	self world mainDockingBars		do: [:each | each delete]! !!Project methodsFor: 'docking bars support' stamp: 'dgd 9/20/2004 16:30'!showWorldMainDockingBar	^ self projectPreferenceFlagDictionary		at: #showWorldMainDockingBar		ifAbsent: [Preferences showWorldMainDockingBar]! !!Project methodsFor: 'docking bars support' stamp: 'dgd 9/6/2004 18:47'!showWorldMainDockingBar: aBoolean 	"Change ther receiver to show the main docking bar"	self projectPreferenceFlagDictionary at: #showWorldMainDockingBar put: aBoolean.	""	self == Project current		ifTrue: [""			aBoolean == Preferences showWorldMainDockingBar				ifFalse: [Preferences setPreference: #showWorldMainDockingBar toValue: aBoolean]].	""	self assureMainDockingBarPresenceMatchesPreference! !!Project methodsFor: 'docking bars support' stamp: 'dgd 9/20/2004 19:28'!showWorldMainDockingBarString	^ (self showWorldMainDockingBar		ifTrue: ['<yes>']		ifFalse: ['<no>'])		, 'show main docking bar (M)' translated! !!Project methodsFor: 'docking bars support' stamp: 'dgd 9/20/2004 16:31'!toggleShowWorldMainDockingBar	self showWorldMainDockingBar: self showWorldMainDockingBar not! !!Project methodsFor: 'language' stamp: 'dgd 9/19/2004 13:27'!updateLocaleDependents	"Set the project's natural language as indicated"	ActiveWorld allTileScriptingElements do: [:viewerOrScriptor |			viewerOrScriptor localeChanged].	Flaps disableGlobalFlaps: false.	Preferences eToyFriendly		ifTrue: [			Flaps addAndEnableEToyFlaps.			ActiveWorld addGlobalFlaps]		ifFalse: [Flaps enableGlobalFlaps].	(Project current isFlapIDEnabled: 'Navigator' translated)		ifFalse: [Flaps enableDisableGlobalFlapWithID: 'Navigator' translated].	ParagraphEditor initializeTextEditorMenus.	Utilities emptyScrapsBook.	MenuIcons initializeTranslations.	LanguageEnvironment localeChanged.	Locale localeChanged.	"self setFlaps.	self setPaletteFor: aLanguageSymbol."! !!RealEstateAgent class methodsFor: 'as yet unclassified' stamp: 'dgd 9/1/2004 16:12'!maximumUsableArea	| allowedArea |	allowedArea _ Display usableArea.	Smalltalk isMorphic ifTrue: [		allowedArea := allowedArea intersect: ActiveWorld visibleClearArea.	].	^allowedArea! !!RealEstateAgent class methodsFor: 'as yet unclassified' stamp: 'dgd 9/1/2004 16:12'!maximumUsableAreaInWorld: aWorldOrNil	| allowedArea |	allowedArea _ Display usableArea.	aWorldOrNil ifNotNil: [		allowedArea := allowedArea intersect: aWorldOrNil visibleClearArea.	].	^allowedArea! !!ScrollPane methodsFor: 'menu' stamp: 'dgd 9/18/2004 18:29'!wantsYellowButtonMenu	"Answer true if the receiver wants a yellow button menu"	^ getMenuSelector notNil! !!SelectedObjectThumbnail methodsFor: 'accessing' stamp: 'dgd 9/10/2004 19:28'!noSelectedBalloonText: aString 	"Set the balloon text to be used when no object is selected"	noSelectedBalloonText := aString! !!SelectedObjectThumbnail methodsFor: 'accessing' stamp: 'dgd 9/10/2004 19:27'!noSelectedThumbnail: aForm 	"Set the form to be used when no object is selected"	noSelectedThumbnail := aForm! !!SelectedObjectThumbnail methodsFor: 'initialization' stamp: 'dgd 9/10/2004 19:00'!initialize	"Initialize the receiver"	super initialize.	""	self		image: (Form extent:32@32).	self color: Color transparent! !!SelectedObjectThumbnail methodsFor: 'initialization' stamp: 'dgd 9/10/2004 19:29'!initializeExtent: aPoint noSelectedThumbnail: aForm noSelectedBalloonText: aString 	self		image: (Form extent: aPoint).""	noSelectedThumbnail := aForm.	noSelectedBalloonText := aString! !!SelectedObjectThumbnail methodsFor: 'stepping and presenter' stamp: 'dgd 9/10/2004 19:29'!step	| current |	current := self selectedObject.	self setBalloonText: (current isNil				ifTrue: [noSelectedBalloonText]				ifFalse: [current externalName]).	""	self makeThumbnailFrom: current! !!SelectedObjectThumbnail methodsFor: 'stepping and presenter' stamp: 'dgd 9/10/2004 18:36'!stepTime	^ 125! !!SelectedObjectThumbnail methodsFor: 'private' stamp: 'dgd 9/10/2004 18:43'!makeEmptyThumbnail^ self makeThumbnailOfColor: Color veryLightGray.! !!SelectedObjectThumbnail methodsFor: 'private' stamp: 'dgd 9/10/2004 18:43'!makeErrorThumbnail	^ self makeThumbnailOfColor: Color red! !!SelectedObjectThumbnail methodsFor: 'private' stamp: 'dgd 9/13/2004 12:57'!makeThumbnailFrom: aMorphOrNil 	| thumbnail |	thumbnail := aMorphOrNil isNil				ifTrue: [noSelectedThumbnail						ifNil: [self makeEmptyThumbnail]]				ifFalse: [aMorphOrNil iconOrThumbnail]. 	""	self		image: (thumbnail scaledIntoFormOfSize: self extent)! !!SelectedObjectThumbnail methodsFor: 'private' stamp: 'dgd 9/10/2004 18:43'!makeThumbnailOfColor: aColor 	| form |	form := Form extent: self extent depth: 32.	form getCanvas fillColor: aColor.	self image: form! !!SelectedObjectThumbnail class methodsFor: 'instance creation' stamp: 'dgd 9/10/2004 19:31'!extent: aPoint noSelectedThumbnail: aForm noSelectedBalloonText: aString 	^ self new		initializeExtent: aPoint		noSelectedThumbnail: aForm		noSelectedBalloonText: aString ! !!SelectionMorph methodsFor: 'accessing' stamp: 'dgd 9/11/2004 21:57'!wantsToBeTopmost	"Answer if the receiver want to be one of the topmost objects in 	its owner"	^ true! !!SelectionMorph methodsFor: 'dropping/grabbing' stamp: 'dgd 9/11/2004 22:00'!justDroppedInto: newOwner event: evt	selectedItems isEmpty ifTrue:		["Hand just clicked down to draw out a new selection"		^ self extendByHand: evt hand].	dupLoc ifNotNil: [dupDelta _ self position - dupLoc].	selectedItems reverseDo: [:m | 		WorldState addDeferredUIMessage:			[m referencePosition: (newOwner localPointToGlobal: m referencePosition).			newOwner handleDropMorph:				(DropEvent new setPosition: evt cursorPoint contents: m hand: evt hand)] fixTemps].	evt wasHandled: true! !!SelectionMorph methodsFor: 'halo commands' stamp: 'dgd 9/20/2004 13:53'!addCustomMenuItems: aMenu hand: aHandMorph	"Add custom menu items to the menu"	super addCustomMenuItems: aMenu hand: aHandMorph.	aMenu addLine.	aMenu add: 'add or remove items' translated target: self selector: #addOrRemoveItems: argument: aHandMorph.	aMenu addList: {		#-.		{'place into a row' translated. #organizeIntoRow}.		{'place into a column' translated. #organizeIntoColumn}.		#-.		{'align left edges' translated. #alignLeftEdges}.		{'align top edges' translated. #alignTopEdges}.		{'align right edges' translated. #alignRightEdges}.		{'align bottom edges' translated. #alignBottomEdges}.		#-.		{'align centers vertically' translated. #alignCentersVertically}.		{'align centers horizontally' translated. #alignCentersHorizontally}.		}.	self selectedItems size > 2		ifTrue:[			aMenu addList: {				#-.				{'distribute vertically' translated. #distributeVertically}.				{'distribute horizontally' translated. #distributeHorizontally}.				}.		].! !!SelectionMorph methodsFor: 'halo commands' stamp: 'dgd 9/25/2004 12:08'!alignBottomEdges	"Make the bottom coordinate of all my elements be the same"	| maxBottom |	selectedItems		ifEmpty: [^ self].	maxBottom := (selectedItems				collect: [:itm | itm bottom]) max.	selectedItems		do: [:itm | itm bottom: maxBottom].	self changed! !!SelectionMorph methodsFor: 'halo commands' stamp: 'dgd 9/20/2004 13:41'!alignCentersHorizontally	"Make every morph in the selection have the same vertical center as the topmost item."	| minLeft leftMost |	selectedItems size > 1 ifFalse: [^ self].	minLeft _ (selectedItems collect: [:itm | itm left]) min.	leftMost _ selectedItems detect: [:m | m left = minLeft].	selectedItems do:		[:itm | itm center: (itm center x @ leftMost center y)].	self changed! !!SelectionMorph methodsFor: 'halo commands' stamp: 'dgd 9/20/2004 13:41'!alignCentersVertically	"Make every morph in the selection have the same horizontal center as the topmost item."	| minTop topMost |	selectedItems size > 1 ifFalse: [^ self].	minTop _ (selectedItems collect: [:itm | itm top]) min.	topMost _ selectedItems detect: [:m | m top = minTop].	selectedItems do:		[:itm | itm center: (topMost center x @ itm center y)].	self changed! !!SelectionMorph methodsFor: 'halo commands' stamp: 'dgd 9/25/2004 12:07'!alignLeftEdges	"Make the left coordinate of all my elements be the same"	| minLeft |	selectedItems		ifEmpty: [^ self].	minLeft := (selectedItems				collect: [:itm | itm left]) min.	selectedItems		do: [:itm | itm left: minLeft].	self changed! !!SelectionMorph methodsFor: 'halo commands' stamp: 'dgd 9/25/2004 12:08'!alignRightEdges	"Make the right coordinate of all my elements be the same"	| maxRight |	selectedItems		ifEmpty: [^ self].	maxRight := (selectedItems				collect: [:itm | itm right]) max.	selectedItems		do: [:itm | itm right: maxRight].	self changed! !!SelectionMorph methodsFor: 'halo commands' stamp: 'dgd 9/25/2004 12:09'!alignTopEdges	"Make the top coordinate of all my elements be the same"	| minTop |	selectedItems		ifEmpty: [^ self].	minTop := (selectedItems				collect: [:itm | itm top]) min.	selectedItems		do: [:itm | itm top: minTop].	self changed! !!SelectionMorph methodsFor: 'halo commands' stamp: 'dgd 9/20/2004 13:50'!distributeHorizontally	"Distribute the empty vertical space in a democratic way."	| minLeft maxRight totalWidth currentLeft space |	self selectedItems size > 2		ifFalse: [^ self].	minLeft := self selectedItems anyOne left.	maxRight := self selectedItems anyOne right.	totalWidth := 0.	self selectedItems		do: [:each | 			minLeft := minLeft min: each left.			maxRight := maxRight max: each right.			totalWidth := totalWidth + each width].	currentLeft := minLeft.	space := (maxRight - minLeft - totalWidth / (self selectedItems size - 1)) rounded.	(self selectedItems		asSortedCollection: [:x :y | x left <= y left])		do: [:each | 			each left: currentLeft.			currentLeft := currentLeft + each width + space].	self changed! !!SelectionMorph methodsFor: 'halo commands' stamp: 'dgd 9/20/2004 13:51'!distributeVertically	"Distribute the empty vertical space in a democratic way."	| minTop maxBottom totalHeight currentTop space |	self selectedItems size > 2		ifFalse: [^ self].	minTop := self selectedItems anyOne top.	maxBottom := self selectedItems anyOne bottom.	totalHeight := 0.	self selectedItems		do: [:each | 			minTop := minTop min: each top.			maxBottom := maxBottom max: each bottom.			totalHeight := totalHeight + each height].	currentTop := minTop.	space := (maxBottom - minTop - totalHeight / (self selectedItems size - 1)) rounded.	(self selectedItems asSortedCollection:[:x :y | x top <= y top])		do: [:each | 			each top: currentTop.			currentTop := currentTop + each height + space].	self changed! !!SelectionMorph methodsFor: 'halos and balloon help' stamp: 'dgd 9/11/2004 18:29'!hasHalo: aBool 	super hasHalo: aBool.	aBool		ifFalse: [ (self hasProperty: #deleting) ifFalse: [self delete] ]! !!SelectionMorph methodsFor: 'initialization' stamp: 'dgd 9/20/2004 13:57'!defaultBorderColor	"answer the default border color/fill style for the receiver"	^ (Preferences menuSelectionColor ifNil: [Color blue]) twiceDarker alpha: 0.75! !!SelectionMorph methodsFor: 'initialization' stamp: 'dgd 8/28/2004 19:00'!defaultBorderWidth	"answer the default border width for the receiver"	^ 2! !!SelectionMorph methodsFor: 'initialization' stamp: 'dgd 9/20/2004 14:00'!defaultColor	"answer the default color/fill style for the receiver"	^ (Preferences menuSelectionColor ifNil: [Color blue]) alpha: 0.08! !!SelectionMorph methodsFor: 'initialization' stamp: 'dgd 9/11/2004 21:07'!extendByHand: aHand	"Assumes selection has just been created and added to some pasteUp or world"	| startPoint handle |	startPoint := self position.	handle := NewHandleMorph new followHand: aHand		forEachPointDo: [:newPoint |					| localPt |					localPt := (self transformFrom: self world) globalPointToLocal: newPoint.					self bounds: (startPoint rect: localPt)				]		lastPointDo: [:newPoint |					selectedItems isEmpty						ifTrue: [self delete]						ifFalse: [							selectedItems size = 1								ifTrue:[self delete.  selectedItems anyOne addHalo]								ifFalse:[self doneExtending]						]				].	aHand attachMorph: handle.	handle startStepping.! !!SelectionMorph methodsFor: 'submorphs-add/remove' stamp: 'dgd 9/11/2004 18:27'!delete	self setProperty: #deleting toValue: true.	super delete.	! !!SelectionMorph methodsFor: 'private' stamp: 'dgd 9/20/2004 14:01'!doneExtending	otherSelection ifNotNil:		[selectedItems _ otherSelection selectedItems , selectedItems.		otherSelection delete.		self setOtherSelection: nil].	self changed.	self layoutChanged.	super privateBounds:		((Rectangle merging: (selectedItems collect: [:m | m fullBounds]))			expandBy: 8).	self changed.	self addHalo.! !!SelectionMorph methodsFor: 'viewer' stamp: 'dgd 8/29/2004 12:34'!externalName	^ 'Selected {1} objects' translated format:{self selectedItems size}! !!SelectionMorph methodsFor: 'WiW support' stamp: 'dgd 9/11/2004 21:58'!morphicLayerNumber	"helpful for insuring some morphs always appear in front of or  	behind others. smaller numbers are in front"	^ 8! !!SketchMorph methodsFor: 'menus' stamp: 'dgd 9/18/2004 19:56'!addToggleItemsToHaloMenu: aCustomMenu 	"Add toggle-items to the halo menu"	super addToggleItemsToHaloMenu: aCustomMenu.	Preferences noviceMode		ifFalse: [""aCustomMenu				addUpdating: #useInterpolationString				target: self				action: #toggleInterpolation]! !!StickyPadMorph methodsFor: 'visual properties' stamp: 'dgd 9/18/2004 18:52'!canHaveFillStyles	"Return true if the receiver can have general fill styles; not just 	colors. This method is for gradually converting old morphs."	^ true! !!SystemWindow methodsFor: 'menu' stamp: 'dgd 9/18/2004 18:27'!wantsYellowButtonMenu	"Answer true if the receiver wants a yellow button menu"	^ false! !!SystemWindow methodsFor: 'thumbnail' stamp: 'dgd 9/22/2004 19:29'!icon	"Answer a form with an icon to represent the receiver"	^ MenuIcons windowIcon! !!TextMorph methodsFor: 'event handling' stamp: 'dgd 9/18/2004 20:06'!mouseDown: evt 	"Make this TextMorph be the keyboard input focus, if it isn't  	already, and repond to the text selection gesture."	evt yellowButtonPressed		ifTrue: ["First check for option (menu) click"			^ self yellowButtonActivity: evt shiftPressed].	evt hand newKeyboardFocus: self.	self		handleInteraction: [editor mouseDown: evt]		fromEvent: evt.! !!TextMorph methodsFor: 'event handling' stamp: 'dgd 8/28/2004 13:54'!wouldAcceptKeyboardFocusUponTab	"Answer whether the receiver might accept keyboard focus if 	tab were hit in some container playfield"	^ self inPartsBin not! !!TheWorldMainDockingBar methodsFor: 'construction' stamp: 'dgd 9/6/2004 18:44'!createDockingBar	"Create a docking bar from the receiver's representation"	| dockingBar |	dockingBar := DockingBarMorph new.	dockingBar adhereToTop.	dockingBar autoGradient: true.	""	self fillDockingBar: dockingBar.	""	^ dockingBar! !!TheWorldMainDockingBar methodsFor: 'construction' stamp: 'dgd 9/22/2004 19:05'!fillDockingBar: aDockingBar 	"Private - fill the given docking bar"	aDockingBar		add: 'Squeak' translated		icon: MenuIcons squeakIcon		help: 'Options related to Squeak as a whole' translated		subMenu: self squeakMenu.	aDockingBar		add: 'Project' translated		icon: MenuIcons homeIcon		help: 'Options to open things in the current project or to navigate between projects' translated		subMenu: self projectMenu.	aDockingBar		add: 'Configuration' translated		icon: MenuIcons configurationIcon		help: 'Options to configure Squeak' translated		subMenu: self configurationMenu.	aDockingBar		add: 'Help' translated		icon: MenuIcons helpIcon		help: 'Helpful options or options to get help' translated		subMenu: self helpMenu.	""	aDockingBar addSpacer.	self fillNavigatorOn: aDockingBar.	aDockingBar addSpacer.	aDockingBar addMorphBack: (ClockMorph new show24hr: true;			 showSeconds: false).	aDockingBar addSpace: 5.	""	aDockingBar		addMorphBack: (self				createButtonIcon: MenuIcons volumeIcon				help: 'Change sound volume' translated				selector: #changeSoundVolume).	aDockingBar		addMorphBack: (self				createButtonIcon: MenuIcons fullScreenIcon				help: (ScreenController lastScreenModeSelected						ifTrue: ['Exit from full screen' translated]						ifFalse: ['Switch to full screen' translated])				selector: #toggleFullScreen).	aDockingBar		addMorphBack: (self				createButtonIcon: (SelectedObjectThumbnail						extent: 37 @ 28						noSelectedThumbnail: MenuIcons objectsIcon						noSelectedBalloonText: 'View objects hierarchy' translated)				selector: #viewSelectedObject).	""	aDockingBar setProperty: #mainDockingBarTimeStamp toValue: self class timeStamp! !!TheWorldMainDockingBar methodsFor: 'construction' stamp: 'dgd 9/22/2004 19:38'!fillNavigatorOn: main 	"main addLine."	main addSpace: 10.	""	main		addMorphBack: (self				createButtonIcon: MenuIcons backIcon				help: 'Previous project'				selector: #previousProject).	main		addMorphBack: (self				createButtonIcon: MenuIcons jumpIcon				help: 'Put up a list of all projects, letting me choose one to go to'				selector: #jumpToProject).	main		addMorphBack: (self				createButtonIcon: MenuIcons forwardIcon				help: 'Next project'				selector: #nextProject).	main		addMorphBack: (self				createButtonIcon: MenuIcons openIcon				help: 'Find any file'				selector: #findAnyFile).	main		addMorphBack: (self				createButtonIcon: MenuIcons loadProjectIcon				help: 'Find a project'				selector: #findAProject).	"main addSpace: 20."	main addSpace: 10.	main addSpacer.	main		addMorphBack: (self				createButtonIcon: MenuIcons projectIcon				help: 'Start a new project'				selector: #newProject).	main		addMorphBack: (self				createButtonIcon: MenuIcons paintIcon				help: 'Make a painting'				selector: #doNewPainting).	main		addMorphBack: (self				createButtonIcon: MenuIcons objectCatalogIcon				help: 'Open the objects catalog'				selector: #activateObjectsTool).	main		addMorphBack: (self				createButtonIcon: MenuIcons publishIcon				help: 'Publish the current project'				selector: #publishProject).	""	main addSpace: 10! !!TheWorldMainDockingBar methodsFor: 'construction' stamp: 'dgd 9/6/2004 14:05'!openDockingBar	^ self createDockingBar openInWorld ! !!TheWorldMainDockingBar methodsFor: 'construction - submenus' stamp: 'dgd 9/22/2004 20:59'!configurationMenu	| menu |	menu := MenuMorph new defaultTarget: self.	""	self createMenuItem: {'set language...'. 'Choose the language in which Squeak should be displayed'. MenuIcons smallLanguageIcon} on: menu.	menu addLine.	self createMenuItem: {'update from server'. 'Update from server (Internet access is required)'. MenuIcons smallUpdateIcon} on: menu.	""	menu addLine.	menu addUpdating: #showWorldMainDockingBarString action: #toggleShowWorldMainDockingBar.	Flaps sharedFlapsAllowed		ifTrue: [menu				addUpdating: #suppressFlapsString				target: CurrentProjectRefactoring				action: #currentToggleFlapsSuppressed].	menu addLine.	ScreenController lastScreenModeSelected		ifTrue: [self createMenuItem: {'exit from full screen'. 'Exit from full screen and enclose Squeak in a window'. MenuIcons smallFullScreenIcon} on: menu]		ifFalse: [self createMenuItem: {'switch to full screen'. 'Switch to full screen giving the maximun display space to Squeak'. MenuIcons smallFullScreenIcon} on: menu].	""	self createMenuItem: {'change sound volume'. 'Change sound volume'. MenuIcons smallVolumeIcon} on: menu.	Preferences noviceMode		ifFalse: [menu addLine.			self createMenuItem: {'preferences..'. 'Opens a "Preferences Panel" which allows you to alter many settings'. MenuIcons smallConfigurationIcon} on: menu].	menu addLine.	Preferences noviceMode		ifTrue: [self createMenuItem: {'switch to expert mode'. 'Switch to expoer mode. CAUTION: The expert mode is powerful as well as dangerous'. MenuIcons smallExpertIcon} on: menu]		ifFalse: [self createMenuItem: {'switch to novice mode'. 'Come back to novice mode'. MenuIcons smallExpertIcon} on: menu].	""	^ menu! !!TheWorldMainDockingBar methodsFor: 'construction - submenus' stamp: 'dgd 9/10/2004 17:17'!helpMenu	| menu |	menu := MenuMorph new defaultTarget: self.	""	self createMenuItem: {'move objects onscreen'. 'Bring back all objects whose current coordinates keep them from being visible, so that at least a portion of each of my interior objects can be seen'} on: menu.	self createMenuItem: {'unhide hidden objects'. 'If any items on the world are currently hidden, make them visible'} on: menu.	menu addLine.	self createMenuItem: {'show all viewers'. 'Make visible the viewers for all objects which have user-written scripts in this playfield'} on: menu.	self createMenuItem: {'hide all viewers'. 'Make invisible the viewers for all objects in the world'} on: menu.	menu addLine.	self createMenuItem: {'clear turtle trails'. 'Remove any pigment laid down on the desktop by objects moving with their pens down'} on: menu.	menu addLine.	self createMenuItem: {'eToy vocabulary summary'. 'Displays a summary of all the pre-defined commands and properties in the pre-defined eToy vocabulary'} on: menu.	""	^ menu! !!TheWorldMainDockingBar methodsFor: 'construction - submenus' stamp: 'dgd 9/22/2004 19:38'!projectMenu	| menu |	menu := MenuMorph new defaultTarget: self.	""	self createMenuItem: {'previous project'. 'Return to the most recently visited project'. MenuIcons smallBackIcon} on: menu.	self createMenuItem: {'jump to project...'. 'Put up a list of all projects, letting me choose one to go to'. MenuIcons smallJumpIcon} on: menu.	self createMenuItem: {'next project'. 'Go to next project'. MenuIcons smallForwardIcon} on: menu.	menu addLine.	self createMenuItem: {'find any file'. 'Import a file into Squeak'. MenuIcons smallImportFileIcon} on: menu.	self createMenuItem: {'find a project'. 'Open a project into Squeak'. MenuIcons smallLoadProjectIcon} on: menu.	menu addLine.	self createMenuItem: {'new project'. 'Start a new project'. MenuIcons smallProjectIcon} on: menu.	self createMenuItem: {'make new drawing'. 'Make a painting'. MenuIcons smallPaintIcon} on: menu.	self createMenuItem: {'object catalog (o)'. 'A tool for finding and obtaining many kinds of objects'. MenuIcons smallObjectCatalogIcon} on: menu.	self createMenuItem: {'object from paste buffer'. 'Create a new object from paste buffer'. MenuIcons smallPasteIcon} on: menu.	menu addLine.	self createMenuItem: {'view objects hierarchy'. 'A tool for discovering the objects and the relations between them'. MenuIcons smallObjectsIcon} on: menu.	menu addLine.	self createMenuItem: {'publish project'. 'Publish the current project'. MenuIcons smallPublishIcon} on: menu.	""	^ menu! !!TheWorldMainDockingBar methodsFor: 'construction - submenus' stamp: 'dgd 9/22/2004 19:07'!squeakMenu	| menu |	menu := MenuMorph new defaultTarget: self.	""	" 	menu add: 'configuration...' translated subMenu: self  	configurationMenu. menu lastItem icon: MenuIcons  	smallConfigurationIcon. menu addLine.  	"	self createMenuItem: {'save'. 'Save the current state of Squeak on disk'. MenuIcons smallSaveIcon} on: menu.	self createMenuItem: {'save as...'. 'Save the current state of Squeak on disk under a new name'. MenuIcons smallSaveAsIcon} on: menu.	Preferences noviceMode		ifFalse: [""			self createMenuItem: {'save as new version'. 'Save the current state of Squeak on disk under a version-stamped name'. MenuIcons smallSaveAsIcon} on: menu].	menu addLine.	self createMenuItem: {'save and quit'. 'Save the current state of Squeak on disk, and quit out of Squeak'. MenuIcons smallQuitIcon} on: menu.	self createMenuItem: {'quit'. 'Quit out of Squeak'. MenuIcons smallQuitIcon} on: menu.	""	^ menu! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/9/2004 18:12'!activateObjectsTool	self world activateObjectsTool! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/4/2004 19:37'!changeSoundVolume	self notYetImplemented! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/9/2004 18:12'!clearTurtleTrails	self world clearTurtleTrails! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/9/2004 18:12'!doNewPainting	| w |	w := self world.	w		assureNotPaintingElse: [^ self].	w		makeNewDrawing: (World primaryHand lastEvent copy setPosition: w center)! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/9/2004 18:12'!eToyVocabularySummary	Cursor wait		showWhile: [self world printVocabularySummary]! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/9/2004 18:35'!exitFromFullScreen	self toggleFullScreen! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/9/2004 18:13'!findAProject	FileList2		morphicViewProjectLoader2InWorld: self world		reallyLoad: true		dirFilterType: #limitedSuperSwikiDirectoryList! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/9/2004 18:13'!findAnyFile	FileList2 morphicViewGeneralLoaderInWorld: self world! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/9/2004 18:13'!hideAllViewers	self hideAllViewersIn: self world! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/9/2004 17:46'!hideAllViewersIn: aMorph 	aMorph isFlapTab		ifTrue: [self hideAllViewersIn: aMorph referent].	aMorph		submorphsDo: [:each | ""			({ScriptEditorMorph. StandardViewer} includes: each class)				ifTrue: [each dismiss]				ifFalse: [self hideAllViewersIn: each]]! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/7/2004 20:52'!importFile	self findAnyFile! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/4/2004 18:43'!jumpToProject	Project jumpToProject! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/7/2004 20:30'!makeNewDrawing	self doNewPainting! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/9/2004 18:13'!moveObjectsOnscreen	self world roundUpStrays! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/4/2004 18:41'!newProject	Project newMorphicOn: nil! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/4/2004 18:56'!nextProject	Project advanceToNextProject.	Beeper beep! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/7/2004 20:30'!objectCatalog	self activateObjectsTool! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/19/2004 13:48'!objectFromPasteBuffer	ActiveHand 	pasteMorph! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/7/2004 21:50'!preferences	Preferences openFactoredPanel! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/4/2004 18:42'!previousProject	Project returnToPreviousProject.	CurrentProjectRefactoring exitCurrentProject.	"go to parent if no previous"	Beeper beep.! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/9/2004 18:16'!publishProject	self		publishStyle: #limitedSuperSwikiPublishDirectoryList		forgetURL: false		withRename: false! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/9/2004 18:15'!publishStyle: aSymbol forgetURL: aBoolean withRename: renameBoolean	| w primaryServer rename |	w _ self world ifNil: [^Beeper beep].	w setProperty: #SuperSwikiPublishOptions toValue: aSymbol.	primaryServer _ w project primaryServerIfNil: [nil].	rename _ ((primaryServer notNil		and: [primaryServer acceptsUploads]) not)		or: [renameBoolean].	w setProperty: #SuperSwikiRename toValue: rename.	w project 		storeOnServerShowProgressOn: self 		forgetURL: aBoolean | rename.! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/7/2004 11:23'!quit	| saveBeforeQuitting |	saveBeforeQuitting := self				confirm: 'Save changes before quitting?' translated				orCancel: [^ self].	""	SmalltalkImage current snapshot: saveBeforeQuitting andQuit: true! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/7/2004 11:20'!save	SmalltalkImage current snapshot: true andQuit: false! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/7/2004 11:25'!saveAndQuit	SmalltalkImage current snapshot: true andQuit: true! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/7/2004 11:21'!saveAs	SmalltalkImage current saveAs! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/22/2004 17:48'!saveAsNewVersion	SmalltalkImage current saveAsNewVersion! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/9/2004 16:23'!setLanguage	Project current chooseNaturalLanguage ! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/9/2004 18:13'!showAllViewers	self world showAllPlayers! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/20/2004 20:03'!showWorldMainDockingBarString	^ self world showWorldMainDockingBarString! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/20/2004 20:04'!suppressFlapsString	"Answer the wording of the suppress-flaps item"	^ CurrentProjectRefactoring suppressFlapsString! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/24/2004 18:39'!switchToExpertMode	| ok |	ok := self confirm: 'CAUTION!!The expert mode is powerful as well as dangerous and you can break your Squeak in several ways.Are you sure to switch to expert mode?' translated.	ok		ifFalse: [^ self].	""	Preferences enable: #cmdGesturesEnabled.	Preferences enable: #debugHaloHandle.	Preferences disable: #noviceMode! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/9/2004 18:36'!switchToFullScreen	self toggleFullScreen! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/20/2004 19:52'!switchToNoviceMode	Preferences disable: #cmdGesturesEnabled.	Preferences disable: #debugHaloHandle.	Preferences enable: #noviceMode.! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/9/2004 18:28'!toggleFullScreen	ScreenController lastScreenModeSelected		ifTrue: [ScreenController new fullScreenOff]		ifFalse: [ScreenController new fullScreenOn].	self world positionSubmorphs.self class updateInstances! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/20/2004 20:05'!toggleShowWorldMainDockingBar	self world toggleShowWorldMainDockingBar! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/9/2004 18:13'!unhideHiddenObjects	self world showHiders! !!TheWorldMainDockingBar methodsFor: 'menu actions' stamp: 'dgd 9/25/2004 12:07'!updateFromServer