ghUp  ifNil:[ChangeHIghUp := false].! !!MCPackageLoader class methodsFor: 'as yet unclassified' stamp: 'edc 10/1/2009 16:29'!setHighestUpdate: aBoolean" MCPackageLoader togleHighestUpdate"self changeHighestUpdate.	^ChangeHIghUp  := aBoolean! !!MCPackageLoader class methodsFor: 'as yet unclassified' stamp: 'edc 9/30/2009 11:07'!useChangeSetNamed: baseName during: aBlock    "Use the named change set, or create one with the given name."    | changeHolder oldChanges newChanges csName |    changeHolder := (ChangeSet respondsTo: #newChanges:)                        ifTrue: [ChangeSet]                        ifFalse: [Smalltalk].    oldChanges := (ChangeSet respondsTo: #current)                        ifTrue: [ChangeSet current]                        ifFalse: [Smalltalk changes].(self class changeHighestUpdate) ifTrue:[csName := (SystemVersion current highestUpdate + 1) asString,baseName.    newChanges := (ChangesOrganizer changeSetNamed: csName) ifNil: [ ChangeSet new name: csName ].    changeHolder newChanges: newChanges.    [aBlock value] ensure: [changeHolder newChanges: oldChanges].SystemVersion current registerUpdate: SystemVersion current highestUpdate + 1]ifFalse:[	newChanges := (ChangesOrganizer changeSetNamed: baseName) ifNil: [ ChangeSet new name: baseName ].	changeHolder newChanges: newChanges.	[aBlock value] ensure: [changeHolder newChanges: oldChanges].]! !!Utilities class methodsFor: 'fetching updates' stamp: 'edc 10/1/2009 16:30'!updateFromServer	"Update the image by loading all pending updates from the server. Also	save local copies of the update files if the #updateSavesFile preference	is set to true"	MCPackageLoader setHighestUpdate: true..	MCMcmUpdater updateFromRepositories: #('http://source.squeak.org/trunk' ).	MCPackageLoader  setHighestUpdate: false..! !MCPackageLoader class removeSelector: #togleHighestUpdate!Object subclass: #MCPackageLoader	instanceVariableNames: 'requirements unloadableDefinitions obsol